        if(index == 0)
          neighbors.splice(3, 5);
        else if(index == this.x - 1){
          neighbors.splice(0, 2);
          neighbors.splice(3, 3);
        }else if(index == this.x * this.y - 1){
          neighbors.splice(0, 4);
          neighbors.splice(3, 1);
        }else if(index == (this.x * this.y) - this.x){
          neighbors.splice(1, 5);
        }else if(index < this.x)
