```
function OffLineMinimum(strArr) { 
  var tempArr = [];
  var result  = [];
  strArr.forEach(function(e) {
    if (/[^E]/.test(e)) {
      tempArr.push(e);
    }
    else {
      result.push(Number(tempArr.sort().shift()));
    }
  });
  return result;
}
